(()=>{"use strict";(e=>{const t=document.getElementById("timer-hours"),n=document.getElementById("timer-minutes"),o=document.getElementById("timer-seconds");let s=setInterval((()=>{let e=(()=>{let e=(new Date("01 january 2022").getTime()-(new Date).getTime())/1e3;return{timeRemaining:e,hours:Math.floor(e/60/60),minutes:Math.floor(e/60%60),seconds:Math.floor(e%60)}})();for(let t in e)switch(!0){case e[t]<10&&e[t]>0:e[t]="0"+e[t];break;case e[t]<=0:e[t]="00"}t.textContent=e.hours,n.textContent=e.minutes,o.textContent=e.seconds,e.timeRemaining<=0&&clearInterval(s)}),1e3)})()})();